import React from "react";
import { NavLink } from "react-router-dom";
import s from "./Dialogs.module.css";

const DialogItem = (props) => {
  return (
    <div className={s.dialog + " " + s.active}>
      <NavLink to={"/dialogs/" + props.id}>{props.username}</NavLink>
    </div>
  );
};

const Message = (props) => {
  return <div className={s.message}>{props.message}</div>;
};

const Dialogs = (props) => {
  let dialogItem = [
    { id: "1", username: "Viktor" },
    { id: "3", username: "Alex" },
    { id: "2", username: "John" },
    { id: "4", username: "Frank" },
    { id: "5", username: "Maxim" },
  ];

  let dialogMessages = [
    { id: "1", message: "Hi" },
    { id: "2", message: "How are you today" },
    { id: "3", message: "All good, how about you?" },
  ];

  let dialogElementsArray = dialogItem.map((item) => (
    <DialogItem id={item.id} username={item.username} />
  ));

  let dialogMess = dialogItem.map((item) => (
    <DialogItem id={item.id} username={item.username} />
  ));

  return (
    <div className={s.dialogs}>
      <div className={s.dialogItems}>{dialogElementsArray}</div>
      <div className={s.messages}>
        <Message message={dialogMessages[0].message} />
        <Message message={dialogMessages[1].message} />
        <Message message={dialogMessages[2].message} />
      </div>
    </div>
  );
};

export default Dialogs;
